from django.core.management.base import BaseCommand
from django.utils import timezone
from assets_ms.models import Component
from decimal import Decimal
from datetime import timedelta


class Command(BaseCommand):
    help = 'Seed the database with 100 component records'

    def add_arguments(self, parser):
        parser.add_argument('--clear', action='store_true', help='Clear existing components before seeding')

    def handle(self, *args, **options):
        if options['clear']:
            self.stdout.write(self.style.WARNING('Clearing existing components...'))
            Component.objects.all().delete()
            self.stdout.write(self.style.SUCCESS('Existing components cleared.'))

        self.stdout.write(self.style.MIGRATE_HEADING('Creating 100 component records...'))
        components_data = self.get_components_data()
        created_count = 0
        skipped_count = 0

        for component_data in components_data:
            component, created = Component.objects.get_or_create(
                name=component_data['name'], model_number=component_data['model_number'], defaults=component_data
            )
            if created:
                created_count += 1
                if created_count % 10 == 0:
                    self.stdout.write(self.style.SUCCESS(f'âœ“ Created {created_count} components...'))
            else:
                skipped_count += 1

        self.stdout.write(self.style.SUCCESS(f'\n{"="*60}'))
        self.stdout.write(self.style.SUCCESS(f'Seeding Complete! Created: {created_count} | Skipped: {skipped_count}'))
        self.stdout.write(self.style.SUCCESS(f'{"="*60}\n'))

